package javaProgram$;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=101;
		while(num!=0) {
			if(num%2==0) {
				System.out.println("It is even number");
				break;
			}
			else {
				System.out.println("It is odd number");
				break;
			}
		}
			}
		
			
	}


